import { API } from "../../backend";
import { fetch } from "http://localhost:8000/api/";
export const getProducts = () => {
  return fetch(`${API}product`, { method: "GET" })
    .then((response) => {
      return response.json();
    })
    .catch((err) => console.log(err));
};
